import React, { useState } from 'react';
import './index.css';
const App = () => {
   const purple = "#8e44ad";
   const [bg,setBg] = useState(purple);

   const bgchange = () =>{
       const bgc = "#800000";
       setBg(bgc);
   }

     
   return(
     <>
        <div style = {{ backgroundColor : bg}}>
            <button onClick ={bgchange}> Click Me</button>
        </div>
     </>
   );
}

export default App;
