import React, { useState } from 'react';
import './index.css';

const App = () => {

    const [name,setName] = useState("");
    const [sname,setSname] = useState("");

    const updatechange = (event) => {
         setName(event.target.value);
    }
 
    const updateschange = (ev) => {
        setSname(ev.target.value);
    }

    const [fullname, setFullName] = useState();
    const submit = () =>{
         setFullName(name + " " + sname);
    }
    return (
        <>
            <div>
                <h1>Hello {fullname} </h1>
                <input type="text" placeholder="Enter Your First Name" onChange = {updatechange}
                value={name}></input>
                <input type="text" placeholder="Enter Your second Name" onChange = {updateschange}
                value={sname}></input>
                <button onClick={submit}> Click me ðŸ˜„</button>
            </div>
        </>
    );
}

export default App;
