import React, { useState } from 'react';
import './index.css';

const App = () => {
    const[details,setDetails] = useState({
        fname : "",
        lname : "",
        email : "",
    });
    const updates = (event) => {
       const name = event.target.name;
       const value = event.target.value;
        setDetails((preValue)=>{
            if(name === 'fName'){
                return{
                   fname : value ,
                   lname : preValue.lname ,
                   email : preValue.email ,
                };
            }else if(name === 'lName'){
                return{
                    fname : preValue.fname ,
                    lname : value ,
                    email : preValue.email,
                };
            }else if(name === "eMail"){
                return{
                    fname : preValue.fname , 
                    lname : preValue.lname , 
                    email : value
                };
            }
        });
        
    };
    const submit = () =>{
        alert("from submitted");
    }
    return(
        <>
           <div>
               <form>
                   <div>
                       <h1> Hello {details.fname} {details.lname} {details.email} </h1>
                       <input type="text" placeholder="Enter your first Name" name="fName" value ={details.fname} onChange={updates} ></input>
                       <input type="text" placeholder="Enter your Second Name" name="lname" value={details.lname} onChange={updates}></input>
                       <input type="text" placeholder="Enter your E-mail" name="eMail" value={details.email} onChange={updates}></input>
                       <button onClick="submit">Click me ðŸ–•</button>
                   </div>
               </form>
           </div>


        </>
    );
}

export default App;
